import { PayloadAction, createSlice } from "@reduxjs/toolkit";
// import CartProduct from "../../types/CartProduct";

interface CartProduct{
    item_id: number
    item_name: string
    item_photo: string
    item_price: number
    quantity:number
  }

export interface CartState {
  cart: CartProduct[];
}

interface IncreaseQuantityPayload {
  item_id: number;
}

interface DecreaseQuantityPayload {
  item_id: number;
}

interface RemoveItemPayload {
  item_id: number;
}

const cartSlice = createSlice({
  name: "cart",
  initialState: { cart: [] } as CartState,
  reducers: {
    addToCart: (state, action) => {
      const itemInCart = state.cart.find(
        (item) => item.item_id === action.payload.item_id
      );
      if (itemInCart) {
        if (itemInCart.quantity !== undefined) {
          itemInCart.quantity++;
        }
      } else {
        state.cart.push({ ...action.payload, quantity: 1 });
      }
    },

    increaseQuantity: (
      state,
      action: PayloadAction<IncreaseQuantityPayload>
    ) => {
      const item = state.cart.find((item) => item.item_id === action.payload.item_id);
      if (item && item.quantity !== undefined) {
        item.quantity++;
      }
    },

    decreaseQuantity: (
      state,
      action: PayloadAction<DecreaseQuantityPayload>
    ) => {
      const item = state.cart.find((item) => item.item_id === action.payload.item_id);
      if (item && item.quantity !== undefined && item.quantity > 1) {
        item.quantity--;
      }
    },

    removeItem: (state, action: PayloadAction<RemoveItemPayload>) => {
      state.cart = state.cart.filter((item) => item.item_id !== action.payload.item_id);
    },
  },
});

export const { addToCart, increaseQuantity, decreaseQuantity, removeItem } =
  cartSlice.actions;

export const artReducer = cartSlice.reducer;
