import { combineReducers } from 'redux';
import {GET_ALL_PRODUCT,GET_NUMBER_CART,ADD_CART, DECREASE_QUANTITY, INCREASE_QUANTITY, DELETE_CART} from  '../actions/action';

interface Data{
  item_id: number
  item_name: string
  item_photo: string
  item_price: number
  quantity:number
}

interface Productint{
    numberCart:number
    Carts:Array<Data>
    _products:Array<Data>
}

const initProduct:Productint = {
    numberCart:0,
    Carts:[],
    _products:[]
}

function todoProduct(state = initProduct,action:any){
    switch(action.type){
        case GET_ALL_PRODUCT: 
            return{
                ...state,
                _products:action.payload
            }
        case GET_NUMBER_CART:
                return{
                    ...state
                }
        case ADD_CART:
            if(state.numberCart===0){
                let cart:Data = {
                    item_id:action.payload.item_id,
                    quantity:1,
                    item_name:action.payload.item_name,
                    item_photo:action.payload.item_photo,
                    item_price:action.payload.item_price
                } 
                state.Carts.push(cart); 
            }
            else{
                let check = false;
                state.Carts.map((item,key)=>{
                    if(item.item_id===action.payload.item_id){
                        state.Carts[key].quantity++;
                        check=true;
                    }
                });
                if(!check){
                    let _cart = {
                        item_id:action.payload.item_id,
                        quantity:1,
                        item_name:action.payload.item_name,
                        item_photo:action.payload.item_photo,
                        item_price:action.payload.item_price
                    }
                    state.Carts.push(_cart);
                }
            }
            return{
                ...state,
                numberCart:state.numberCart+1
            }
            case INCREASE_QUANTITY:
                state.numberCart++
                state.Carts[action.payload].quantity++;

               return{
                   ...state
               }
            case DECREASE_QUANTITY:
                let quantity = state.Carts[action.payload].quantity;
                if(quantity>1){
                    state.numberCart--;
                    state.Carts[action.payload].quantity--;
                }

                return{
                    ...state
                }
            case DELETE_CART:
                let quantity_ = state.Carts[action.payload].quantity;
                return{
                    ...state,
                    numberCart:state.numberCart - quantity_,
                    Carts:state.Carts.filter(item=>{
                        return item.item_id!=state.Carts[action.payload].item_id
                    })

                }
        default:
            return state;
    }
}
const ShopApp = combineReducers({
    _todoProduct:todoProduct
});
export default ShopApp;
