import { Dispatch } from '@reduxjs/toolkit';
// import {callApi} from '../api/axiosInstance'
import { createAsyncThunk } from '@reduxjs/toolkit';
import axiosInstance from '../api/axiosInstance'
export const INCREASE_QUANTITY = 'INCREASE_QUANTITY';
export const DECREASE_QUANTITY = 'DECREASE_QUANTITY';
export const GET_ALL_PRODUCT = 'GET_ALL_PRODUCT';
export const GET_NUMBER_CART = 'GET_NUMBER_CART';
export const ADD_CART = 'ADD_CART' ;
export const UPDATE_CART = 'UPDATE_CART';
export const DELETE_CART = 'DELETE_CART';

interface Data{
    createdAt: Date
  deletedAt: Date
  isdeleted: boolean
  item_desc: String
  item_id: number
  item_name: string
  item_photo: string
  item_price: number
  updatedAt: Date
}

export const Fetch = createAsyncThunk("Fetch", async () => {
    console.log();

    const response = await axiosInstance.post("/fetchdetail");
    const resData = response.data;
    console.log(resData);

    return (dispatch:Dispatch) =>{ dispatch(GetAllProduct(resData))};
  });

/*GET_ALL_PRODUCT*/
export function GetAllProduct(payload:Data){
    return{
        type:'GET_ALL_PRODUCT',
        payload
    }
}

/*GET NUMBER CART*/
export function GetNumberCart(){
    return{
        type:'GET_NUMBER_CART'
    }
}

export function AddCart(payload:Data){
    return {
        type:'ADD_CART',
        payload
    }
}
export function UpdateCart(payload:Data){
    return {
        type:'UPDATE_CART',
        payload
    }
}
export function DeleteCart(payload:Data){
    return{
        type:'DELETE_CART',
        payload
    }
}

export function IncreaseQuantity(payload:Data){
    return{
        type:'INCREASE_QUANTITY',
        payload
    }
}
export function DecreaseQuantity(payload:Data){
    return{
        type:'DECREASE_QUANTITY',
        payload
    }
}
